import { Markup } from 'interweave'
import React, { useState } from 'react'
import styled from 'styled-components'


function Brief(props) {

    const [button, setbutton] = useState("description")
    console.log(props.items.description.en)
    return (
        <>
            {props.items.description.en && <Container>
                <Buttons>
                    <button onClick={() => { setbutton('description') }} >Description</button>
                    <button onClick={() => { setbutton('marketdata') }} >Market Data</button>
                    <button onClick={() => { setbutton('news') }} >News</button>
                </Buttons>
                <MiniContainer>
                    {button === "description" && <Markup content={props.items.description.en} />}
                    {button === "marketdata" &&
                        <MarketData>
                           <p>24H Low : {props.items.market_data.high24H}</p>

                        </MarketData>

                    }



                </MiniContainer>
            </Container>}
        </>
    )
}

export default Brief

const Container = styled.div`
margin: 2rem;
background-color: white;
box-shadow: 0 2px 6px 0 rgba(173,175,182,03);
// width: %;
overflow-y: scroll;
0334080299
`
const Buttons = styled.div`

button{
    outline: none;
    border:none;
    background-color: rgba(241,241,241,1);
    padding: 1rem;

    &:hover{
        background-color: grey;
    }
}

`
const MiniContainer = styled.div`
margin:1rem 2rem;
`
const MarketData = styled.div``
