import React, { useEffect, useState } from 'react'
import styled from 'styled-components'


function Trading() {

    const [items, setItems] = useState([])



    useEffect(() => {
        fetch("https://api2.binance.com/api/v3/ticker/24hr")
            .then(res => res.json())
            .then(
                (result) => {
                    //   setIsLoaded(true);
                    setItems(result);
                    // console.log(result)
                },
                (error) => {
                    console.log(error)
                }
            )

    }, [])
    console.log(items)

    return (
        <>
        <h2>Top Gainers</h2>
        <TradingContainer>
            <Card>
                <Info>
                    <h3>BTC</h3>
                </Info>

                <PriceChange>
                    <h2>$ 65,000</h2>
                    <p> -2.55% </p>
                </PriceChange>
            </Card>
            <Card>
                <Info>
                    <h3>ETH</h3>
                </Info>

                <PriceChange>
                    <h2>$ 10,000</h2>
                    <p> +3.55% </p>
                </PriceChange>
            </Card>

            <Card>
                <Info>
                    <h3>SHIB</h3>
                </Info>

                <PriceChange>
                    <h2>$ 0.006000</h2>
                    <p> -2.55% </p>
                </PriceChange>
            </Card>

            <Card>
                <Info>
                    <h3>0TC</h3>
                </Info>

                <PriceChange>
                    <h2>$ 5,000</h2>
                    <p> +5.55% </p>
                </PriceChange>
            </Card>

            <Card>
                <Info>
                    <h3>BOT</h3>
                </Info>

                <PriceChange>
                    <h2>$ 300</h2>
                    <p> +55% </p>
                </PriceChange>
            </Card>
        </TradingContainer>
        </>
    )
}

export default Trading

const TradingContainer= styled.div`
display: flex;
flex-wrap: wrap;
padding: 0 3rem;
`

const Card = styled.div`
background-color: white;
width: fit-content;
padding:0.5rem;
border-radius: 0.5rem;
margin:1rem;
box-shadow: 1px 10px 20px black;
cursor: pointer;
transition: all 0.17s ease-in-out;
&:hover{
    transform: scale(1.1)
}
`

const Info = styled.div`
`

const PriceChange = styled.div`
display: flex;
align-items: end;
position: relative;
bottom: 0;

p{
    margin: 1rem;
    color: red;
}
`
