import React, { useEffect, useState } from 'react'
import styled from 'styled-components'


function Allcrypto() {

    const [items, setItems] = useState([])
    const [loading, setLoading] = useState(true)

 setTimeout(() => {
     

         fetch("https://api2.binance.com/api/v3/ticker/24hr" , { mode: 'no-cors'})
            .then(res => res.json())
            .then(
                (result) => {
                    setLoading(false);
                    setItems(result);
                    // console.log(result)
                },
                (error) => {
                    setLoading(true)
                    console.log(error)
                }
            )
           
        }, 500);

    return (
        <Allcryp>

            <thead>
                <th>#</th>
                <th>NAME</th>
                <th>PRICE</th>
                <th>24H CHANGE</th>
                <th>24H VOLUME</th>
                <th></th>
            </thead>
            {loading && <Loader> <img src="./images/Circle-Loading.svg" /></Loader>}

            {items.map((item, index) => {
                return (
                    <Home>
                        <tr>
                            <td><Price> {index} </Price></td>
                            <td>{item.symbol}</td>
                            <td>${item.weightedAvgPrice}</td>
                            <td style={{ color: Math.sign(item.priceChangePercent) === -1 ? "red" : "green" }}>{item.priceChangePercent}%</td>
                            <td>${item.volume}</td>
                            <td> <button>Trade</button> </td>
                        </tr>
                    </Home>)
            })}
        </Allcryp>
    )
}

export default Allcrypto
const Allcryp = styled.div`
// padding: 4rem 15rem;
margin: 4rem;
th{
    width:15rem;
padding:1rem;
font-size: 0.8rem;
font-weight: 400;
}
td{
    background:white;
width:15rem;
padding:1rem;
font-size: 0.8rem;
font-weight: 700;
color: rgb(109,100,127)

}
`
const Home = styled.div`
border-top: 1px solid rgb(237,242,247);
border-bottom: 1px solid rgb(237,242,247);
transition: all 0.10s ease-in-out;
cursor:pointer;

button{
    border:none;
    outline:none;
    background-color:rgb(17,153,250);
    padding:0.2rem 1rem;
    border-radius:1rem;

    &:hover{
        background-color:rgb(17,153,200)
    }
}
&:hover{
    box-shadow: 0 0px 8px 2px black;
    tranform: scale(1.05);
}
`
const Symbol = styled.p`
`

const Loader = styled.div`
margin-left: 45%;
`
const Price = styled.p`
font-weight: 600;`
