import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router';
import { Link } from 'react-router-dom';
import styled from 'styled-components'


function Allcrypto() {

    const [items, setItems] = useState([])
    const [loading, setLoading] = useState(true)
    const [more, setMore] = useState(20)

    useEffect(() => {
        fetch("https://api2.binance.com/api/v3/ticker/24hr")
            .then(res => res.json())
            .then(
                (result) => {
                    setLoading(false);
                    setItems(result);
                },
                (error) => {
                    setLoading(true)
                    console.log(error)
                }
            )
    }, [])
    const loadMore = () => {
        return setMore(more + 20)
    }
    return (
        <Allcryp>

            <thead>
                <th>#</th>
                <th>NAME</th>
                <th>PRICE</th>
                <th>24H CHANGE</th>
                <th>24H VOLUME</th>
                <th></th>
            </thead>
            {loading && <Loader> <img src="./images/Circle-Loading.svg" /></Loader>}

            {items.slice(0, more).map((item, index) => {
                return (
                    <Home>
                        <tr key={item.symbol}>
                            <td><Price> {index + 1} </Price></td>
                            <td>{item.symbol}</td>
                            <td>${item.weightedAvgPrice}</td>
                            <td style={{ color: Math.sign(item.priceChangePercent) === -1 ? "red" : "green" }}>{item.priceChangePercent}%</td>
                            <td>${item.volume}</td>
                            <td> <Link  to={`/trade/:${item.symbol}`}> <button >Trade</button> </Link> </td>
                        </tr>
                    </Home>)
            })}
            <NextPrev><button onClick={loadMore}  >More</button></NextPrev>

        </Allcryp>

    )
}

export default Allcrypto
const Allcryp = styled.div`
// padding: 4rem 15rem;
margin: 4rem;
th{
    width:15rem;
padding:1rem;
font-size: 0.8rem;
font-weight: 400;
}
td{
    background:white;
width:15rem;
padding:1rem;
font-size: 0.8rem;
font-weight: 700;
color: rgb(109,100,127)

}

@media  (max-width: 600px) {
 
   th{
       width:0rem;
   }
   
   td{
       width:0rem;
   }
  }
`
const Home = styled.div`
border-top: 1px solid rgb(237,242,247);
border-bottom: 1px solid rgb(237,242,247);
transition: all 0.10s ease-in-out;
cursor:pointer;

button{
    border:none;
    outline:none;
    background-color:rgb(17,153,250);
    padding:0.2rem 1rem;
    border-radius:1rem;

    &:hover{
        background-color:rgb(17,153,200)
    }
}
&:hover{
    box-shadow: 0 0px 8px 2px black;
    tranform: scale(1.05);
}
`
const Symbol = styled.p`
`

const Loader = styled.div`
margin-left: 45%;
`
const Price = styled.p`
font-weight: 600;`

const NextPrev = styled.div`

button{
    margin: 2rem 50%;
    border: none;
    outline:none;
    background-color:rgb(157,152,152);
    padding: 0.5rem 1rem;
cursor: pointer;
    &:hover{
        background-color: rgb(157,152,200)
    }
}
`
