import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import styled from 'styled-components'




function Fulldata() {

    const [items, setItems] = useState([])
    const [loading, setLoading] = useState(true)
    let { Id } = useParams()
    Id = Id.replace(":", "")

    useEffect(() => {
        fetch(`https://api.coingecko.com/api/v3/coins/${Id}`)
            .then(res => res.json())
            .then(
                (result) => {
                    setItems(result);
                    setLoading(false);
                },
                (error) => {
                    setLoading(true)
                    console.log(error)
                }
            ).catch((error) => {
                setLoading(true)
                console.log(error)
            })
    }, [])
    console.log(items)


    console.log(Id)
    return (<>
        {loading && <Loader> <img src="./Circle-Loading.svg" /></Loader>}
        {!loading && <Detail>
            <BasicInfo>

                <Name>
                    Name:  {items.name}
                </Name>
                <Price>
                    Current Price : $ {items.market_data.current_price.usd}
                </Price>
                <MarketCap>
                    Market Cap : $ {items.market_data.market_cap.usd}
                </MarketCap>
                <Volume>
                    Total Volume : {items.market_data.total_volume.usd}
                </Volume>
            </BasicInfo>

        </Detail>}
    </>
    )
}

export default Fulldata

const Detail = styled.div`
height:90vh;

`

const BasicInfo = styled.div`
background-color: white;
padding: 1rem;
width: fit-content;
margin: 1rem;
box-shadow: 0 2px 6px 0 
`


const Name = styled.p``
const Price = styled.p``
const MarketCap = styled.p``
const Volume = styled.p``

const Loader = styled.div`
margin-left: 45%;
`
const PriceChange = styled.table``
