import React, { useEffect, useRef, useState } from 'react'
import { Line } from 'react-chartjs-2'
import Chartjs from "./Chart.js"


function Chart(props) {

    const [data, setdata] = useState()
    useEffect(() => {

        fetch(`https://api.coingecko.com/api/v3/coins/${props.items.id}/market_chart?vs_currency=usd&days=1`).then(res => res.json())
            .then(
                result => {
                    setdata(result)
                }).catch(error => { console.log(error) })
    }, [])

    const chartRef = useRef()
    const myChart = new Chartjs(chartRef.current, {

        type: Line,
        data: {
            labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
            datasets: [{
                label: '# of Votes',
                data: [12, 19, 3, 5, 2, 3],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
            }]
        },
        options: {
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }

    })

    console.log(data)


    const [historicaldata, sethistoricaldata] = useState();
    const [days, setdays] = useState(1)

    return (
        <div>

            <canvas ref={chartRef} id="myChart" width={200} height={400}>

            </canvas>


        </div>
    )
}

export default Chart
