import React, { useEffect, useState } from 'react'
import styled from 'styled-components'



function Allcrypto() {

    const [items, setItems] = useState([])
     const [loading , setLoading] =useState(false)


    useEffect(() => {
        fetch("https://api2.binance.com/api/v3/ticker/24hr")
            .then(res => res.json())
            .then(
                (result) => {
                      setLoading(true);
                    setItems(result);
                    // console.log(result)
                },
                (error) => {
                    console.log(error)
                }
            )
    }, [])

    return (
        <Allcryp>

            <thead>
                <th>#</th>
                <th>NAME</th>
                <th>PRICE</th>
                <th>24H CHANGE</th>
                <th>24H VOLUME</th>
                <th></th>
            </thead>
            {items.map((item, index) => {
                return (
                    <Home>
                        <tr>
                            <td>{index}</td>
                            <td>{item.symbol}</td>
                            <td>${item.weightedAvgPrice}</td>
                            <td>{item.priceChangePercent}%</td>
                            <td>${item.volume}</td>
                            <td> <button>Trade</button> </td>
                        </tr>

                    </Home>)
            })}
        </Allcryp>
    )
}

export default Allcrypto
const Allcryp = styled.div`
background:white;
padding: 4rem 15rem;
th{
    width:10rem;
padding:1rem;
}
td{
width:10rem;
padding:1rem;
}
`
const Home = styled.div`
button{
    border:none;
    outline:none;
    background-color:rgb(17,153,250);
    padding:0.2rem 1rem;
    border-radius:1rem;

    &:hover{
        background-color:rgb(17,153,200)
    }
}
`
const Symbol = styled.p`
`
