import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import Allcrypto from './Allcrypto'


function Trading() {

    const [items, setItems] = useState([])



    useEffect(() => {
        fetch("https://api2.binance.com/api/v3/ticker/24hr")
            .then(res => res.json())
            .then(
                (result) => {
                    //   setIsLoaded(true);
                    setItems(result);
                    // console.log(result)
                },
                (error) => {
                    console.log(error)
                }
            )

    }, [])
    console.log(items)

    return (
        <>
            <Heading>Top Movers</Heading>
            <TradingContainer>
                <Card>
                    <img src="https://crypto.com/price/_next/image.png?url=https%3A%2F%2Ftpp-static.crypto.com%2Ftoken%2Ficons%2Fbitcoin%2Fcolor_icon.png&w=32&q=75" />
                    <Info>
                        <h3>BTC</h3>
                    </Info>

                    <PriceChange>
                        <h2>$ 65,000</h2>
                        <p> -2.55% </p>
                    </PriceChange>
                </Card>
                <Card>
                    <img src="https://crypto.com/price/_next/image.png?url=https%3A%2F%2Ftpp-static.crypto.com%2Ftoken%2Ficons%2Fethereum%2Fcolor_icon.png&w=32&q=75" />
                    <Info>
                        <h3>ETH</h3>
                    </Info>

                    <PriceChange>
                        <h2>$ 10,000</h2>
                        <p> +3.55% </p>
                    </PriceChange>
                </Card>

                <Card>
                    <img src="https://crypto.com/price/_next/image.png?url=https%3A%2F%2Ftpp-static.crypto.com%2Ftoken%2Ficons%2Fshiba-inu%2Fcolor_icon.png&w=32&q=75" />
                    <Info>
                        <h3>SHIB</h3>
                    </Info>

                    <PriceChange>
                        <h2>$ 0.006000</h2>
                        <p> -2.55% </p>
                    </PriceChange>
                </Card>

                <Card>
                    <img src="https://crypto.com/price/_next/image.png?url=https%3A%2F%2Ftpp-static.crypto.com%2Ftoken%2Ficons%2Fsolana%2Fcolor_icon.png&w=32&q=75" />
                    <Info>
                        <h3>Solana</h3>
                    </Info>

                    <PriceChange>
                        <h2>$ 5,000</h2>
                        <p> +5.55% </p>
                    </PriceChange>
                </Card>

            </TradingContainer>

            <Allcrypto />
        </>
    )
}

export default Trading

const TradingContainer = styled.div`
display: flex;
flex-wrap: wrap;
padding: 0 3rem;
`

const Card = styled.div`
background-color: white;
width: 18rem;
padding:0.5rem;
border-radius: 0.2rem;
margin:0.7rem;
box-shadow: 2px 5px 15px black;
cursor: pointer;
transition: all 0.17s ease-in-out;
&:hover{
    transform: scale(1.1)
}
`

const Info = styled.div`

h3{
    font-size: 1rem;
    font-family: 'Roboto Mono', monospace;
}
`

const PriceChange = styled.div`
display: flex;
justify-content: space-between;
align-items: end;
position: relative;
bottom: 0;
padding:-02rem;
h2{
    font-size: 1.5rem;
}
p{
    margin: 1rem;
    color: red;
    font-weight:bold;
}
`
const Heading = styled.div``
