import React, { useEffect, useState } from 'react'
import styled from 'styled-components'



function Allcrypto() {

    const [items, setItems] = useState([])
    const [loading, setLoading] = useState(true)


    useEffect(() => {
        fetch("https://api2.binance.com/api/v3/ticker/24hr")
            .then(res => res.json())
            .then(
                (result) => {
                    setLoading(false);
                    setItems(result);
                    // console.log(result)
                },
                (error) => {
                    setLoading(true)
                    console.log(error)
                }
            )
    }, [])

    return (
        <Allcryp>

            {loading && <img src="./images/Circle-Loading.svg" />}
            <thead>
                <th>#</th>
                <th>NAME</th>
                <th>PRICE</th>
                <th>24H CHANGE</th>
                <th>24H VOLUME</th>
                <th></th>
            </thead>
            {items.map((item, index) => {
                return (
                    <Home>
                        <tr>
                            <td>{index}</td>
                            <td>{item.symbol}</td>
                            <td>${item.weightedAvgPrice}</td>
                            <td>{item.priceChangePercent}%</td>
                            <td>${item.volume}</td>
                            <td> <button>Trade</button> </td>
                        </tr>
                    </Home>)
            })}
        </Allcryp>
    )
}

export default Allcrypto
const Allcryp = styled.div`

// padding: 4rem 15rem;
margin: 4rem;
th{
    width:15rem;
padding:1rem;
}
td{
    background:white;
width:15rem;
padding:1rem;
}
`
const Home = styled.div`
border-top: 1px solid rgb(237,242,247);
border-bottom: 1px solid rgb(237,242,247);
transition: all 0.10s ease-in-out;
cursor:pointer;

button{
    border:none;
    outline:none;
    background-color:rgb(17,153,250);
    padding:0.2rem 1rem;
    border-radius:1rem;

    &:hover{
        background-color:rgb(17,153,200)
    }
}
&:hover{
    box-shadow: 0 0px 8px 2px black;
    tranform: scale(1.05);
}
`
const Symbol = styled.p`
`
