import React, { useState } from 'react'
import styled from 'styled-components'
const parse = require('html-react-parser');

function Brief(props) {

    const [button, setbutton] = useState()
    console.log(props.items.description.en)
    return (
        <>
            <Container>
                <Buttons>
                    <button onClick={() => { setbutton('description') }} >Description</button>
                    <button onClick={() => { setbutton('marketdata') }} >Market Data</button>
                    <button onClick={() => { setbutton('news') }} >News</button>
                </Buttons>
                <MiniContainer>
                    parse(<div>  {props.items.description.en}</div>)
                </MiniContainer>


            </Container>
        </>
    )
}

export default Brief

const Container = styled.div`
margin: 2rem;
background-color: white;
box-shadow: 0 2px 6px 0 rgba(173,175,182,03);
width: 100%;
overflow-y: scroll;

`
const Buttons = styled.div`
position: sticky;
button{
    outline: none;
    border:none;
    background-color: rgba(241,241,241,1);
    padding: 1rem;

    &:hover{
        background-color: rgba(241,241,255,1)
    }
}

`
const MiniContainer = styled.div`
position : relative;
top: 3rem;
left: 1rem;
`
